OUTPUT_PATH = Path(__file__).parent
ASSETS_PATH = OUTPUT_PATH / Path(r"assets\frame0")


def relative_to_assets(path: str) -> Path:
    return ASSETS_PATH / Path(path)


window = Tk()

window.geometry("633x278")
window.configure(bg = "#FF9090")
window.title('Dear Diary - Setings')
window.iconbitmap("Files/icon.ico")

canvas = Canvas(
    window,
    bg = "#FF9090",
    height = 278,
    width = 633,
    bd = 0,
    highlightthickness = 0,
    relief = "ridge"
)

def pers_record(action, hour, minute):
    print(0)
    act=action.get()
    hr=hour.get()
    mi=minute.get()

    tim=hr+':'+mi
    with open('Files/personal_info.json', 'r') as fin:
        data=json.load(fin)
    d={}
    if 'info' not in data:
        d['info'] = {}

    d['info']['action']=act
    d['info']['time']=tim

    with open('Files/personal_info.json', 'w') as fin:
        json.dump(d, fin, indent=4)
        print(1)

    subprocess.Popen(['python', 'gui.py'])
    window.quit()

