def opens(entry_1,entry_2,entry_3):
    date=entry_1.get()
    mon=entry_2.get()
    year=entry_3.get()
    date_string = f'{year}-{mon}-{date}'
    with open('Files/records.json', 'r') as fin:
        data=json.load(fin)
    comp=False
    for k in data:
        if date_string==k:
            comp=True
    if comp==True:
        d={}
        d[date_string] = {}
        # / ==================================================================================
        d[date_string]['day']=data[date_string]['day']
        d[date_string]['resp_a']=data[date_string]['resp_a']
        d[date_string]['resp_b']=data[date_string]['resp_b']
        d[date_string]['resp_c']=data[date_string]['resp_c']
        d[date_string]['resp_d']=data[date_string]['resp_d']
        d[date_string]['notes']=data[date_string]['notes']
        d[date_string]['score']=data[date_string]['score']

        with open('Files/open_temp_records.json', 'w') as fina:
            json.dump(d, fina, indent=4)

        subprocess.Popen(['python', 'GUI/Single/gui.py'])
        window.quit()

    else:
        subprocess.Popen(['python', 'GUI/Unknown/build/gui.py'])
        window.quit()

